console.clear();
const input = document.getElementById('input');
let cards = document.querySelector('.cards');
let temp = qa;
console.log(temp);
let lang = ['ua', 'ru', 'kz', 'en', 'pl'];
const langSection = document.querySelector('.lang');

let type = ['top-zaprosu', 'quik', 'all-zaprosu', 'top-old', 'hello', 'poland', 'reason', 'give-me', 'action', 'instructions', 'objection'];
const typeSection = document.querySelector('.type');


langSection.addEventListener('click', (e) => {
    if (e.target.tagName === 'INPUT' && e.target.type === 'radio') {
        if (e.target.value == 'all') {
            lang = ['ua', 'ru', 'kz', 'en', 'pl'];
            temp = qa.filter(item => item.question.toLocaleLowerCase().includes(input.value.toLowerCase())).filter(item => lang.includes(item.lang)).filter(item => type.includes('top-zaprosu') && type.includes('quik') ? type.includes(item.type) : item.type.includes(type));
            renderHTML(temp);
        } else {
            lang = e.target.value;
            temp = qa.filter(item => item.question.toLocaleLowerCase().includes(input.value.toLowerCase())).filter(item => item.lang.includes(lang)).filter(item => type.includes(item.type));
            renderHTML(temp);
        }

    }
})

typeSection.addEventListener('click', (e) => {
    if (e.target.tagName === 'INPUT' && e.target.type === 'radio') {
        if (e.target.value == 'allType') {
            type = ['top-zaprosu', 'quik', 'all-zaprosu', 'top-old', 'hello', 'poland', 'reason', 'give-me', 'action', 'instructions', 'objection'];
            temp = qa.filter(item => item.question.toLocaleLowerCase().includes(input.value.toLowerCase())).filter(item => lang.includes(item.lang)).filter(item => type.includes(item.type));
            renderHTML(temp);
        } else {
            type = e.target.value;
            temp = qa.filter(item => item.question.toLocaleLowerCase().includes(input.value.toLowerCase())).filter(item => lang.includes('ua') && lang.includes('ru') ? lang.includes(item.lang) : item.lang.includes(lang)).filter(item => type.includes(item.type));
            renderHTML(temp);
        }

    }
})

const toHTML = (item) => {
    return `<div class="card">
    <div class="question">
        <div class="qu">
            <h1 class="q">Q: </h1>
            <h1>${item.question}</h1>
        </div>    
        <i class="fa-solid fa-copy" onclick="copyText(event)"></i>
    </div>
    <div class="answer">
        <h1 class="a">A: </h1>
        <h1 class="copy-text">${item.answer}</h1>
    </div>
</div>`}

const renderHTML = (arr) => {
    cards.innerHTML = arr.map(toHTML).join(' ');
}

input.addEventListener('input', () => {
    if (input.value == '') {
        renderHTML([])
    } else {
        temp = qa.filter(item => item.question.toLocaleLowerCase().includes(input.value.toLowerCase())).filter(item => lang.includes('ua') && lang.includes('ru') ? lang.includes(item.lang) : item.lang.includes(lang)).filter(item => type.includes('top-zaprosu') && type.includes('quik') ? type.includes(item.type) : item.type.includes(type));
        renderHTML(temp);
    }

})

const ShowAlert = document.querySelector('.alert');

function copyText(event) {
    var card = event.target.closest('.card');
    var answerElement = card.querySelector('.copy-text');

    var range = document.createRange();
    range.selectNodeContents(answerElement);

    var selection = window.getSelection();
    selection.removeAllRanges();
    selection.addRange(range);
    document.execCommand('copy');
    selection.removeAllRanges();
    ShowAlert.classList.add('show')
    setTimeout(() => {
        ShowAlert.classList.remove('show')
    }, 1000)
}


const btns = document.querySelectorAll('.btn');

function offBtn(arr) {
    arr.forEach(item => {
        if (item.classList.contains('active')) {
            item.classList.remove('active')
        }
    })
}

btns.forEach(btn => btn.addEventListener('click', (e) => {
    offBtn(btns)
    e.target.classList.toggle('active')

    cards.style.gridTemplateColumns = `${`${100 / e.target.textContent}% `.repeat(e.target.textContent)}`
}))

const eraser = document.querySelector('.fa-eraser');
eraser.addEventListener('click', () => {
    renderHTML([])
    input.value = '';
    temp = qa;
})
